<template>
  <v-app>
    <v-main>
      <HeaderAndNav></HeaderAndNav>
      <router-view
        :key="$route.fullPath"
        :keycloak="keycloak"
        userName="propsTaro"
      ></router-view>
    </v-main>
  </v-app>
</template>

<script>
import Keycloak from 'keycloak-js';

import HeaderAndNav from './components/HeaderAndNav';
import * as wjcCore from '@grapecity/wijmo';

import '@grapecity/wijmo.styles/wijmo.css';
import '@grapecity/wijmo.vue2.grid';
import '@grapecity/wijmo.vue2.grid.grouppanel';
import '@grapecity/wijmo.vue2.grid.filter';
import '@grapecity/wijmo.vue2.grid.search';
import '@grapecity/wijmo.vue2.core';

// wjcCore.setLicenseKey(
//   '692771655227224#'
// );
// 配布用（配布用で開発も可）
wjcCore.setLicenseKey(
  't-sct1wb00.fukushikenbunroku.com,298536378579113#'
);

export default {
  name: 'App',
  props: [],
  components: {
    HeaderAndNav,
  },
  data() {
    return {
      keycloak: {},
    };
  },
  mounted() {
    let str = location.href.match(/\/([^/]+)\/?$/)[1];
    if (str.match(/#/)) {
      str = str.substring(0, str.indexOf('#'));
    }
    let initOptions = {
      url: 'http://localhost:8884/',
      realm: str,
      clientId: 'account',
      onLoad: 'login-required',
      CheckLoginiframe: false,
    };
    const keycloak = new Keycloak(initOptions);
    this.keycloak = keycloak;
    keycloak
      .init({
        onLoad: initOptions.onLoad,
        checkLoginIframe: initOptions.CheckLoginiframe,
      })
      .then((auth) => {
        console.log(auth);
        if (!auth) {
          window.location.reload();
        }
      })
      .catch(() => {
        //  Vue.$log.error("Authenticated Failed");
      });

    //Token Refresh
    setInterval(() => {
      keycloak
        .updateToken(60)
        .then((refreshed) => {
          if (refreshed) {
            //createApp.$log.info('Token refreshed' + refreshed);
          } else {
            //createApp.$log.info('Token not refreshed, valid for ' + Math.round(keycloak.tokenParsed.exp + keycloak.timeSkew - new Date().getTime() / 1000) + ' seconds');
          }
          // keycloak.clearToken()
        })
        .catch(() => {
          //   Vue.$log.error('Failed to refresh token');
          console.log('Failed to refresh token');
        });
    }, 10000);
  },
  methods: {},
};
</script>
